    /**
     * dfs
     *
     * @param obstacleGrid
     * @return
     */
    public List<List<Integer>> pathWithObstacles(int[][] obstacleGrid) {
        if (obstacleGrid == null || obstacleGrid.length == 0) {
            return new ArrayList<>();
        }
        LinkedList<Pair> list = new LinkedList<>();
        list.add(new Pair(0, 0));
        dfs(obstacleGrid, 0, 0, list);
        return res;
    }

    List<List<Integer>> res = new ArrayList<>();

    private boolean dfs(int[][] grid, int i, int j, LinkedList<Pair> pathIndex) {
        if (grid[i][j] == 1) {
            return false;
        }
        if (i == grid.length - 1 && j == grid[0].length - 1) {
            for (Pair pair : pathIndex) {
                res.add(Arrays.asList(pair.x, pair.y));
            }
            return true;
        }
        if (i + 1 < grid.length && grid[i + 1][j] != 1) {
            pathIndex.add(new Pair(i + 1, j));
            boolean found = dfs(grid, i + 1, j, pathIndex);
            pathIndex.removeLast();
            if (found) {
                return true;
            }
        }
        if (j + 1 < grid[0].length && grid[i][j + 1] != 1) {
            pathIndex.add(new Pair(i, j + 1));
            boolean found = dfs(grid, i, j + 1, pathIndex);
            pathIndex.removeLast();
            if (found) {
                return true;
            }
        }
        // 关键点记录这条路径已经在之前走过是不可达的，即从该点出发是不能到达终点的，就直接标记
        grid[i][j] = 1;
        return false;
    }

    class Pair {
        int x;
        int y;

        public Pair(int x, int y) {
            this.x = x;
            this.y = y;
        }
    }
